          phoneNumber: userPhone,
          name: name
      
        };
        
    
        try {
            const response = await axios.post('http://localhost:5000/api/users/apply', applyInfo);
             localStorage.setItem("token",response.data)
             alert("Applied succesfuly")    
            
          } catch (error) {
            alert(error)
            
          }
        }

    const{name,sallary}=props
